    propName: {
      type: Type,
      default: defaultValue,
      required: false
    }
  },

  data() {
    return {
      // Component data
    }
  },

  computed: {
    // Computed properties
  },

  methods: {
    // Methods
  }
}
</script>

<style scoped>
/* Component styles */
/* IMPORTANT: Do NOT use @apply in styles. Write CSS rules directly. */
</style></ComponentFile></ComponentArtifact>

